mapper.select:
select
  comment_id, content, create_date, article_id, author_id, parent_id, to_uid, level
from msblogapp.blog_comment


        and comment_id = #{commentId}


        and content = #{content}


        and create_date = #{createDate}


        and article_id = #{articleId}


        and author_id = #{authorId}


        and parent_id = #{parentId}


        and to_uid = #{toUid}


        and level = #{level}
